CREATE TABLE IF NOT EXISTS `agent_promotions` (`guid` CHAR(36) BINARY , `promoter_user_account` VARCHAR(255), `promotee_user_account` VARCHAR(255), `brand_role_code` VARCHAR(255), `brand_guid` VARCHAR(255), `status` TINYINT(1), `create_time` VARCHAR(255), `agree_time` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`guid`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `brand_role_meta` (`id` INTEGER auto_increment , `brand_role_code` VARCHAR(255), `key` VARCHAR(255), `value` VARCHAR(255), `type` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `frozen_agents` (`id` INTEGER auto_increment , `agent_guid` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_brand_meta` (`id` CHAR(36) BINARY , `pmp_brand_id` CHAR(36) BINARY, `key` VARCHAR(255), `value` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_brands` (`id` CHAR(36) BINARY , `access_token` VARCHAR(128), `app_key` VARCHAR(18), `name` VARCHAR(255), `company` VARCHAR(255), `status` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_labels` (`id` CHAR(36) BINARY , `pmp_brand_id` CHAR(36) BINARY, `type` VARCHAR(255), `name` VARCHAR(255) UNIQUE, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, UNIQUE `pmp_labels_name_unique` (`name`), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_option_items` (`id` CHAR(36) BINARY , `pmp_specification_option_id` CHAR(36) BINARY, `name` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_product_labels` (`id` CHAR(36) BINARY , `pmp_product_id` CHAR(36) BINARY, `pmp_label_id` CHAR(36) BINARY, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, UNIQUE `plunique` (`pmp_product_id`, `pmp_label_id`), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_product_prices` (`id` CHAR(36) BINARY , `pmp_product_id` CHAR(36) BINARY, `brand_role_code` VARCHAR(255), `price` DECIMAL(10,2), `price_unit` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_products` (`id` CHAR(36) BINARY , `pmp_brand_id` CHAR(36) BINARY, `name` VARCHAR(255), `on_sell` TINYINT(1), `description` VARCHAR(511), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_specification_options` (`id` CHAR(36) BINARY , `pmp_brand_id` CHAR(36) BINARY, `name` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_specifications` (`id` CHAR(36) BINARY , `pmp_variant_id` CHAR(36) BINARY, `name` VARCHAR(255), `on_sell` TINYINT(1), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_variant_images` (`id` CHAR(36) BINARY , `pmp_variant_id` CHAR(36) BINARY, `attachment_id` INTEGER, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_variants` (`id` CHAR(36) BINARY , `pmp_product_id` CHAR(36) BINARY, `name` VARCHAR(255), `on_sell` TINYINT(1), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_goods` (`id` CHAR(36) BINARY , `pmp_specification_id` CHAR(36) BINARY, `goods_code` VARCHAR(255) UNIQUE, `pmp_outcome_count_id` CHAR(36) BINARY, `status` VARCHAR(255) DEFAULT 'PRO', `resell_user_account` VARCHAR(255), `owner_user_account` VARCHAR(255), `seller_user_account` VARCHAR(255), `sold_time` DATETIME, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, UNIQUE `pmp_goods_goods_code_unique` (`goods_code`), PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_goods_meta` (`id` CHAR(36) BINARY , `pmp_goods_id` CHAR(36) BINARY, `key` VARCHAR(255), `value` VARCHAR(255), `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `pmp_outcome_counts` (`id` CHAR(36) BINARY , `pmp_specification_id` CHAR(36) BINARY, `goods_code` VARCHAR(255), `counter_user_account` VARCHAR(255), `count_time` DATETIME, `detial_id` CHAR(36) BINARY, `pmp_outcome_count_id` CHAR(36) BINARY, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB;
